  top: calc(100vh - 42rem) !important;
  left: calc(50% - 22vw) !important;
  position: absolute;
  background-size: cover;
  transition: transform 0.3s cubic-bezier(.99,.9,.8,1.12);
  transform: translate3d(0, 0, 0) rotate(-90deg);
  transform-origin: bottom;

  @include desktop {
    width: 15rem;
    height: 40rem;
    top: calc(100vh - 38rem) !important;
    left: calc(50% - 20rem) !important;
  }

  @include desktop--S {
    width: 15rem;
    height: 40rem;
    top: calc(100vh - 38rem) !important;
    left: calc(50% - 15rem) !important;
  }

  @include tablet {
    width: 13rem;
    height: 35rem;
    top: calc(100vh - 32rem) !important;
    left: calc(50% - 8rem) !important;
  }

  @include phone {
    width: 8rem;
    height: 22rem;
    top: calc(100vh - 20rem) !important;
    left: calc(50%) !important;
  }
}

.subject--rotated {
  transform: rotate(0deg) translate3d(0, 0, 0);
}

