  letter-spacing: 0.1rem;
  transition: left 0.5s ease-in-out;
  box-shadow: 1px 1px 8px rgba($color: #000000, $alpha: 0.25);
  border-radius: 3px;

  @include desktop--S {
    left: 50% !important;
  }

  @include tablet {
    top: 0 !important;
    left: 150vw !important;
    width: 100%;
    height: calc(var(--vh, 1vh) * 110);
    padding-bottom: calc(var(--vh, 1vh) * 10);
  }

  & p {
    margin: 0;
    padding: 50px 30px;
    max-height: 80%;
    overflow: scroll;
  }

  &.invitation--hidden {
    left: calc(100vw) !important;
  }
}

.invitation--hidden {
  transition: transform 0.8s;
  transform: translateX(800px);
}

.inscription {
  position: absolute;
  color: #d0739f;
  background: #fcf3f7;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  margin: auto;
  line-height: 40px;
  text-align: center;
  border-radius: 3px;
  clip-path: circle(0px at 50% 90%);
  transition: clip-path 0.4s ease-in-out;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-evenly;
  padding: 10px 0 10px 10px;
  font-size: 22px;
  color: black;
  caret-color: #d0739f;

  @include phone {
    padding-bottom: 10vh;
    padding-left: 30px;
    padding-right: 30px;
  }
}

.inscription--open {
  clip-path: circle(100%);
}

#switch {
  width: 150px;
  height: 40px;
  top: 88%;
  border-radius: 50px;
  text-align: center;
  background: white;
  user-select: none;
  color: #d0739f;
  line-height: 140%;
  transition: all 0.1s;
}

#switch:hover {
  cursor: pointer;
}

.visible {
  opacity: 1;
  animation-delay: 0.5s;
}

input {
  background: white;
  outline: none;
  border: none;
  vertical-align: middle;

  &::placeholder {
    color: #ebadca;
  }
}

@media screen and (min-color-index: 0) and(-webkit-min-device-pixel-ratio:0) {
  input {
    font-family: arial !important;
    padding: 0 8px !important;
    font-size: 18px;
  }
}

input[type="text"],
input[type="number"],
input[type="mail"] {
  border: 1.5px solid #d0739f;
  border-radius: 50px;
  padding: 0 10px;
  padding-bottom: 8px;
  width: 130px;
  transition: all 0.3s cubic-bezier(0.53, 1.6, 0.36, 1.03);
  line-height: normal;
  height: 30px;

  &[pattern]:hover {
    width: 150px;
  }

  &[pattern]:focus {
    width: 170px !important;
  }

  &:not(:placeholder-shown):not(:focus):invalid {
    border: 1.5px solid crimson;
    color: crimson;
    background: white;
  }

  &:not(:focus):valid {
    border: 1.5px solid #d0739f;
    width: 150px;
    color: #d0739f !important;
    background: white;
  }
}

@keyframes focus {
  to {
    stroke-dashoffset: 0;
  }
}

.inscription .title,
.radioContainer {
  color: #d0739f;
  user-select: none;
}

.radioContainer:hover {
  cursor: pointer;
}

.radioContainer .radio {
  margin-bottom: 10px;
  text-align: left;
  position: relative;
}

.radioContainer .radio::before {
  content: "";
  display: inline-block;
  width: 10px;
  height: 10px;
  background: white;
  border: 1px solid #e0b8b8;
  border-radius: 10px;
  vertical-align: middle;
  margin-right: 12px;
  position: relative;
  transform: scale(1);
  top: -2px;
  transition: border-radius 0.2s, border 0.2s, background 0.2s,
    transform 0.05s linear;
}

.checked::before {
  background: #d0739f !important;
  border: 1px solid #d0739f !important;
  border-radius: 2px !important;
  transform: scale(1.3) !important;
  transition: transform 0.2s cubic-bezier(0.53, 2.9, 0.36, 1.03) !important;
}

@keyframes bounce {
  to {
    transform: scale(1.4);
  }
}

.inscription .button {
  width: 105px;
