#invitation {
  position: absolute;
  left: calc(100vw - 600px) !important; 
  top: calc(50% - 300px) !important;
  height: 600px;
  width: 500px;
  font-size: 22px;
  background: #D0739F;
  padding: 50px;
  font-family: Bellerose;
  color: white;
  line-height: 1.6rem;
  text-align: justify;
  letter-spacing: .1rem;
  transition: left 0.5s ease-in-out;
  box-shadow: 1px 1px 8px rgba($color: #000000, $alpha: 0.25);
  border-radius: 3px;

  @include desktop--S {
    left: 50% !important;
  }

  @include tablet {
    top: 0 !important;
    left: 0 !important;
    width: 100%;
    height: 100%;
    height: calc(var(--vh, 1vh) * 100);
  }

  & p {
    margin: 0;
  }

  &.invitation--hidden {
    left: calc(100vw) !important; 

    @include tablet {
      left: 0 !important;
    }
  }
}

.inscription {
  position: absolute;
  color: #D0739F;
  background: #FCF3F7;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  margin: auto;
  line-height: 40px;
  text-align: center;
  border-radius: 3px;
  clip-path: circle(10px at 50% 90%);
  transition: clip-path 0.4s ease-in-out;

  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-evenly;
  padding: 10px 0 10px 10px;
  font-size: 22px;
  color: black;
  caret-color: #D0739F;
}

.inscription--open {
  clip-path: circle(100%);
}

#switch {
  position: absolute;
  width: 150px;
  height: 40px;
  top: 88%;
  left: 50%;transform: translateX(-50%);
  border-radius: 50px;
  text-align: center;
  background: white;
  user-select: none;
  color: #D0739F;
  line-height: 140%;
  transition: all 0.1s;
}

#switch:hover {
  cursor: pointer;
}

.visible {
  opacity: 1;
  animation-delay: 0.5s;
}

input {
  background: white;
  outline: none;
  border: none;
  vertical-align: middle;

  &::placeholder {
    color: #EBADCA;
  }
}

input[type="text"], input[type="number"], input[type="mail"] {
  border: 1.5px solid #D0739F;
  border-radius: 50px;
  padding: 0 10px; padding-bottom: 8px;
  width: 130px;
  transition: all 0.3s cubic-bezier(.53,1.6,.36,1.03);
  line-height: normal;
  height: 30px;

  &[pattern]:hover {
    width: 150px;
  }

  &[pattern]:focus {
    width: 170px !important;
  }

  &:not(:placeholder-shown):not(:focus):invalid {
    border: 1.5px solid crimson;
    color: crimson;
    background: white;
  }

  &:not(:focus):valid {
    border: 1.5px solid #D0739F;
    width: 150px;
    color: #D0739F !important;
    background: white;
  }
}

@keyframes focus {
  to {
    stroke-dashoffset: 0;
  }
}

.inscription .title, .radioContainer {
  color: #D0739F;
  user-select: none;
}

.radioContainer:hover {
  cursor: pointer;
}

.radioContainer .radio {
  margin-bottom: 10px;
  text-align: left;
  position: relative;
}

.radioContainer .radio::before {
  content: '';
  display: inline-block;
  width: 10px; height: 10px;
  background: white;
  border: 1px solid #E0B8B8;
  border-radius: 10px;
  vertical-align: middle;
  margin-right: 12px;
  position: relative;
  transform: scale(1);
  top: -2px;
  transition: border-radius .2s, border .2s, background .2s, transform .05s linear;
}

.checked::before {
  background: #D0739F !important;
  border: 1px solid #D0739F !important;
  border-radius: 2px !important;
  transform: scale(1.3) !important;
  transition: transform .2s cubic-bezier(.53,2.9,.36,1.03) !important;
}

@keyframes bounce {
  to {
    transform: scale(1.4);
  }
}

.inscription .button {
  width: 105px; height: 35px;
  border-radius: 3px;
  text-align: center;
  line-height: 22px;
  border: 1px solid #D0739F;
  color: #D0739F;
  background: white;
  transition: all 0.2s linear;
}

.inscription .disabled {
  border: 1px solid #D0739F;
  background: transparent;
  color: #D0739F;
  box-shadow: none;
}

.inscription .button:not(.disabled):hover {
  cursor: pointer;
  box-shadow: 3px 3px 8px rgba(0, 0, 0, 0.19);
} 

.inscription .button::not(.disabled):active {
  box-shadow: none;
}

.programme {
  color: #D0739F;
}

.programme:hover {
  cursor: pointer;
}

sources.scss/elements/program.scss:
#program {
  position: absolute;
  height: 110%;
  width: 600px;
  left: calc(100vw - 600px) !important; 
  top: -5% !important;
  background: white;
  border-radius: 3px;
  transition: left 0.5s ease-in-out;
  overflow: auto;
  padding-top: 20px;
  box-shadow: -2px 0 14px rgba($color: #000000, $alpha: 0.2);

  & header {
    width: 595px;
    height: 100px;
    top: 0;
  }

  @include tablet {
    width: 100%;
    height: 100%;
    left: 0 !important;
    top: 0 !important;

    & header {
      height: 100px;
    }

    & * {
      font-size: 20px !important;
    }

    & header {
      width: 100%;
    }
  }

  @include phone {
    & * {
      font-size: 18px !important;
    }
  }
}

#program.program--hidden {
  left: 100vw !important;
}

#program nav {
  width: 85%;
  height: 45px;
  font-family: 'Bellerose';
  display: flex;
  align-items: center;
  justify-content: space-around;
  margin-left: 20px;

  & p, sup {
    position: relative;
    font-size: 24px !important;

    @include tablet {
      font-size: 22px !important;
    }

    @include phone {
      font-size: 16px !important;
    }
  }

  & p::after {
    content: "";
    background: #D0739F;
    width: 0;
    height: 3px;
    position: absolute;
    bottom: -10px;
    left: 0;
    display: inline-block;
    transform-origin: left;
    transition: width 0.3s;
  }

  & p:hover {
    cursor: pointer;
  }

  & sup {
    pointer-events: none;
  }

  & p.day--selected::after {
    width: 100%;
  }
}

.program--content {
  padding: 0 50px;

  @include phone {
    padding: 0 20px;
  }
}

#program h1 {
  font-family: Bellerose;
  font-size: 35px !important;
  text-align: center;
  white-space: nowrap;
    word-wrap: nowrap;
}

#program ul {
  margin: 10px 0;
}

#program li {
  position: relative;
  list-style: none;
}

#program li::before {
  content: '';
  display: inline-block;
  background: #D0739F;
  width: 10px;
  height: 10px;
  border-radius: 100px;
  position: absolute;
  left: -20px;
  top: 50%;
}

#program p, #program li {
  font-size: 25px;
  font-family: Bellerose;
  color: #000000;

  & span {
    color: #D0739F;
  }
}

.close--program {
  position: absolute;
  left: 50%; bottom: 100px;
  transform: translateX(-50%);
  background: no-repeat url('./../ressources/second_section/close.svg');
  background-size: 40%;
  background-position: center;
  background-color: #FCF3F7;
  width: 50px;
  height: 50px;
  border-radius: 50px;
  transition: all 0.2s;

  @include tablet {
    bottom: 50px;
  }

  @include phone {
    bottom: 120px;
  }
}

.close--program:hover {
  cursor: pointer;
  box-shadow: 1px 1px 15px rgba($color: #000000, $alpha: 0.08);
}

sources.scss/sections/loader.scss:
#loader {
  z-index: 100;
  position: absolute;
  overflow: hidden;
  width: 100vw;
  height: 100vh;
  background: #FCF3F7;
  display: flex;
  justify-content: center;
  align-items: center;

  & .text {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    font-family: Bellerose;
    text-align: center;
    color: #D0739F;
    font-size: 40px;
  }

  & svg {
    animation: beat 1.2s infinite cubic-bezier(.78,.41,.61,2.43) alternate-reverse;
    transition: all 0.3s;
    width: 600px;

    @include tablet {
      width: 500px;
    }

    @include phone {
      width: 300px;
    }
  }
}

.fadeOut {
  transition: opacity 0.5s;
  opacity: 0;
}

@keyframes beat {
  from {
    transform: scale(1);
  }
  20%, 100% {
    transform: scale(1.1);
  }
}
